    private void consultarListaProductos() {
       // ConexionSqlHelper con=new ConexionSqlHelper(this,"bd_Tienda",null,2 );
        SQLiteDatabase db= conn.getReadableDatabase();
        Producto productos=null;
        Bitmap bitmap = null;
        Cursor cursor=db.rawQuery("SELECT * FROM "+utiles.TABLA_PRODUCTO,null);

        while (cursor.moveToNext())
        {
            productos= new Producto();
            productos.setId(cursor.getInt(0));
            productos.setNombre(cursor.getString(1));
            productos.setDescripcion(cursor.getString(2));


            productos.setImagen(cursor.getString(3));
            productos.setDetalle(cursor.getString(4));
            listaProductos.add(productos);


        }


    }




